drop table library_books if exists;
drop table book if exists;
drop table library if exists;
create table book (id_book bigint generated by default as identity, author varchar(255), genre varchar(255), isbn varchar(255), nomber_page integer not null, title varchar(255), primary key (id_book));
create table library (id_library varchar(255) not null, address_city varchar(255), address_number integer, address_postal_code integer, address_street varchar(255), library_director_nom varchar(255), library_director_prenom varchar(255), library_type varchar(255), primary key (id_library));
create table library_books (libraryjpa_id_library varchar(255) not null, books_id_book bigint not null);



